import React, {useState, Dispatch} from 'react';
import logo from './logo.svg';
import './App.css';
import LayOut from './Components/LayOut';
import {
  Routes,
  Route,
} from "react-router-dom";
import LandingPage from './Pages/LandingPage';
import Register from './Pages/Register';
import Products from './Pages/Products';
import ViewProduct from './Pages/ViewProduct';
import Cart from './Pages/Cart'
import SignUp from './Pages/SignUp';
import SignIn from './Pages/SignIn';
import {useReducer, createContext} from 'react'
import { StripeCardNumberElementChangeEvent } from '@stripe/stripe-js';



enum ActionType {
  SET_CART = "SET_CART",
}

interface IReducer {
  type: ActionType
  count: number
}

interface ICounter {
  result: number
}

const initialState: ICounter = {
  result: 0,
}

const countValue: number = 1
export const Store = createContext<{
  state: ICounter;
  dispatch: Dispatch<ActionType>; //action type here
}>({
  state: initialState,
  dispatch: () => null,
});

const reducer: React.Reducer<ICounter, IReducer> = (state, action) => {
  switch (action.type) {
    case ActionType.SET_CART:
      return { result: state.result }
    default:
      return state
  }
}

 function App() {
  const [state, dispatch] = React.useReducer<React.Reducer<ICounter, Dispatch<ActionType>>>(
    reducer,
    initialState)
    return (  
      <Store.Provider value={{ state, dispatch}}>
        <LayOut>
        <Routes>
            <Route path='/' element={<LandingPage />}/>
            <Route path='/allProducts' element={<Products category={'men'} />}/>
            <Route path='/register' element={<Register />}/>
            <Route path='/viewproduct' element={<ViewProduct />}/>
            <Route path='/cart' element={<Cart />}/>
            <Route path='/signup' element={<SignUp />}/>
            <Route path='/signin' element={<SignIn />}/>
        </Routes> 
        </LayOut>
    </Store.Provider>
  );
}

export default App;
