import React, { useEffect, useState } from 'react'
import {database} from "../firebase-config"
import { getDatabase, ref, onValue} from "firebase/database";
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';
import CartCard from '../Components/CartCard'
import Divider from '@mui/material/Divider';

interface Inventory {
  picture: string,
  entries: string[],
  count: number
}
interface InventoryEntry{
  [key:string]: Inventory
}
interface Product  {
  picture: string,
  title: string,
  price: string
}
interface ProductEntry {
  [key:string]: Product
}
let total:number = 0
let obj: InventoryEntry = {}

function Cart() {
  const [cart, setCart] =  useState<ProductEntry>({})
  useEffect(() => {
    // Update the document title using the browser API
    const starCountRef = ref(database, 'shopping/');
    onValue(starCountRef, (snapshot) => {
      const data = snapshot.val();
      setCart(data)

      console.log('*******************')

      Object.entries(cart).forEach(func)
      function func(value: [string, Product]){

         if(!obj.hasOwnProperty(value[1].title))
          obj[value[1].title] = {
            picture: value[1].picture,
            entries: [value[0]],
            count: 1
          }
          else{
            let arr :string[] = [...obj[value[1].title].entries] 
            arr.push(value[0])

            obj[value[1].title].entries = [...arr]
            (obj[value[1].title]).count += 1; 
            }
          } 
          Object.entries(obj).map((data)=>{console.log(data)})

    });
  },[]);

  return (
    <Box sx={{ display: 'flex', flexGrow: 1 }}>

       <Grid container  >
        {Object.entries(cart).map((data, index) => (
          <Grid item sx={{width: "90%"}} key={index}>
            <CartCard entryid={data[0]} title={data[1].title} picture={data[1].picture}  price={data[1].price}/>
            <Divider />
          </Grid>
        ))}
      </Grid> 
    </Box>
  )
}

export default Cart
