import React, { useEffect, useState } from 'react'
import {database} from "../firebase-config"
import { getDatabase, ref, onValue} from "firebase/database";
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';
import CartCard from '../Components/CartCard'
import Divider from '@mui/material/Divider';

interface Product  {
  picture: string,
  title: String,
  price: string
}
interface ProductEntry {
  [key:string]: Product
}
let total:number = 0

function Cart() {
  const [cart, setCart] =  useState<ProductEntry>({})
  useEffect(() => {
    // Update the document title using the browser API
    const starCountRef = ref(database, 'shopping/');
    onValue(starCountRef, (snapshot) => {
      const data = snapshot.val();
      setCart(data)
      let arr = [...Object.values(data)]
      console.log('*******************')
      for(let i = 0; i < arr.length; i++){
          console.log(arr[i])
          /* if( arr.findLastIndex((ele)=>{ arr[i][1].price === })) */
      }

      console.log(data)

      /* Object.entries(cart).forEach((ele) => {console.log(total 
        += parseInt(ele[1].price))}) */
    });
  },[]);

  return (
    <Box sx={{ display: 'flex', flexGrow: 1 }}>

       <Grid container  >
        {Object.entries(cart).map((data, index) => (
          <Grid item sx={{width: "90%"}} key={index}>
            <CartCard entryid={data[0]} title={data[1].title} picture={data[1].picture}  price={data[1].price}/>
            <Divider />
          </Grid>
        ))}
      </Grid> 
    </Box>
  )
}

export default Cart
