import React from 'react'
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import {useLocation} from 'react-router-dom';
import Button from '@mui/material/Button';
import { getDatabase, ref, set } from "firebase/database";
import {database} from "../firebase-config"


 type Product =  {
    description: String,
    picture: string,
    title: String,
    price: String
} 

function ViewProduct() {
    const location = useLocation().state as Product;
    const {description,
        picture,
        title,
        price } = location
    const handleCheckout = (e:any) =>{
        e.preventDefault()
        set(ref(database, 'shopping/'+(new Date()).getTime()), {
            picture: picture,
            title: title,
            price : price
        }); 
    }

  return (
    <Box sx={{display: 'flex', m:4, p: 5}}>
        <Box sx={{ order: 1 }}>
        <Box
            component="img"
            sx={{
            height: 400,
            width: 400,
            maxHeight: { xs: 350, md: 400, lg: 800 },
            maxWidth: { xs: 350, md: 400, lg: 800 },
            }}
            alt="The house from the offer."
            src={location.picture}
            />

        </Box>
        <Box sx={{ order: 2, m:4, width: "50%"}}>
            <Typography variant="h6">{title}</Typography>
            <Typography variant="h4">${price}</Typography>
            <Typography variant="body1">{description}</Typography>
            <Button variant="contained" onClick={(e)=>{handleCheckout(e)}}>Check Out</Button>
        </Box>
    </Box>
  )
}

export default ViewProduct
